//رمز بازی
let kalame = Math.floor(Math.random()*670)+2 ;
let ramz = kalamat[kalame] ;
console.log(ramz)



// تعیین سطح بازِی
let tain_sath = document.querySelector('button#b2');
let radio_button = document.querySelector('.radio_button');
tain_sath.addEventListener('click', ()=>{
    radio_button.classList.toggle('active')
    
}
);



//پیشنهاد ربات بر اساس سختی بازی    
let r1 = document.querySelector('#r1')
let r2 = document.querySelector('#r2')
let r3 = document.querySelector('#r3')
let a = document.querySelector('#inputs');
let b;


    let pishnahad_robot = setInterval(()=>{

        
        if( r1.checked ){

            while(true){

                b = kalamat[Math.floor(Math.random()*670)+2]

                if (b.includes(ramz[0])||b.includes(ramz[1])||b.includes(ramz[2])||b.includes(ramz[3])||b.includes(ramz[4])){
                    b=b
                    break; 
                }
            } 


        }else if( r2.checked ){

            b = kalamat[Math.floor(Math.random()*670)+2]

        }else if(r3.checked){

            while(true){

                b = kalamat[Math.floor(Math.random()*670)+2]
                
                if ( !(b.includes(ramz[0])||b.includes(ramz[1])||b.includes(ramz[2])||b.includes(ramz[3])||b.includes(ramz[4])) ) {
                    b=b
                    break; 
                }
            } 



        }


        let robot = document.createElement('p')

        robot.innerHTML =`
        <span id='s0'> ${b[0]} </span>
        <span id='s1'> ${b[1]} </span>
        <span id='s2'> ${b[2]} </span>
        <span id='s3'> ${b[3]} </span>
        <span id='s4'> ${b[4]} </span>
        ` 
        a.insertBefore(robot, a.firstElementChild);


        for (let i=0 ; i<5 ; i++){ 

            if (b === ramz) {
                document.querySelector(`#s${i}`).style.color = 'green';
                continue;
            }else if (b[i] === ramz[i]){        
                document.querySelector(`#s${i}`).style.color = 'green';
            }else if(ramz.includes(b[i])){
                document.querySelector(`#s${i}`).style.color = 'yellow';
            }
                
        }

    },3000)








let inputs = Array.from(document.querySelectorAll('.form1>input')) ;
let dokme = document.querySelector('button');

let chap_kalame = dokme.addEventListener('click',()=>{
        let hads_karbar= `${inputs[0].value}${inputs[1].value}${inputs[2].value}${inputs[3].value}${inputs[4].value}`.toUpperCase();
    
  

        if (hads_karbar.length === 5 ){
            let x = document.createElement('p');
            x.innerHTML =`
            <span id='s0'>${hads_karbar[0]} </span>
            <span id='s1'>${hads_karbar[1]} </span>
            <span id='s2'>${hads_karbar[2]} </span>
            <span id='s3'>${hads_karbar[3]} </span>
            <span id='s4'>${hads_karbar[4]} </span>
            ` 
            let y = document.querySelector('#inputs');
            x.style['background-color']= 'rgb(161, 146, 255)';
            y.insertBefore(x , y.firstElementChild);
            


            for (let i=0 ; i<5 ; i++){ 

                if (hads_karbar === ramz) {
                    document.querySelector(`#s${i}`).style.color = 'green';
                    document.querySelector('.alert').classList.add('active2')
                    document.querySelector('#b1').setAttribute('disabled','true')
                    document.querySelector('#b2').setAttribute('disabled','true')
                    clearInterval(pishnahad_robot)
                    
                }else if (hads_karbar[i] === ramz[i]){        
                    document.querySelector(`#s${i}`).style.color = 'green';                   
                }else if(ramz.includes(hads_karbar[i])){
                    document.querySelector(`#s${i}`).style.color = 'yellow';
                }
                    
            }
    
    
        }else{
            let hoshdar = document.querySelector('#hoshdar')
            let p_hoshdar =document.createElement('p');
            p_hoshdar.textContent = 'Fill in all the fields!'
            p_hoshdar.style['color'] = 'red'
            hoshdar.insertBefore(p_hoshdar,hoshdar.firstElementChild)
            setTimeout(()=> hoshdar.removeChild(p_hoshdar),3000)
        }
});

